class ShowmobileEvent extends BaseShowmobileEvent
{
    public function getBands() {
        return Doctrine_Core::getTable('ShowmobileEventBand')->getWithBands($this->getId());
    }

    public function listBands($limit=3,$delimiter=', ') {
        //$bands = $this->getBands();
        foreach($this->getBands() AS $x => $band) {
          if($x >= $limit) break;
          echo $band->getName().($x < $limit)?$delimiter:' and more';
        }
    }
}
